class OrdProMatPri extends BaseOrdProMatPri
{
  protected $codcomp = '#####';
  protected $nomcomp = 'Materia Prima Adicional';

  public function getNombre()
  {
    $articulo = Doctrine::getTable('Articulo')->findOneBy('codigo', $this->artcomp);
    if($articulo) return $articulo->getNombre();
    else return '<No Encontrado>';
  }

  public function getExistencia()
  {
    $articulo = $this->getMateriaPrima();
    if($articulo) return $articulo[0]->getExistencia();
    else return '<No Encontrado>';
  }

  public function getCodcomp()
  {
    return $this->codcomp;
  }

  public function setCodcomp($value)
  {
    $this->codcomp = $value;
  }

  public function getNomcomp()
  {
    return $this->nomcomp;
  }

  public function setNomcomp($value)
  {
    $this->nomcomp = $value;
  }
}
